import { useState} from 'react'
import { useDispatch, useSelector} from 'react-redux'

import { addNewPost } from '../features/posts/postsSlice'

const Form = () => {
    const dispatch = useDispatch()
    const { user } = useSelector((state) => state.users)
    
    const [title, setTitle] = useState('')
    const [ text, setText] = useState('')
    
    const [ image, setImage] = useState()

    const author = user.nickname

    const onTitleChanged = e => setTitle(e.target.value)
    const onTextChanged = e => setText(e.target.value)
   
    const onImageChanged = e => setImage(e.target.value)

    const [ addRequestStatus, setAddRequestStatus] = useState('idle')

    const canSave = [ title, text].every(Boolean) && addRequestStatus === 'idle'

    const onSaveClicked = () => {
      
        if (canSave) {
            try {
                setAddRequestStatus('pending')
                dispatch(addNewPost({"title":title, "body": text, "author": author, "image": image}))

                setTitle('')
                setText('')
               
            } catch (err) {
                console.error('Failed to save the post', err)
            } finally {
                setAddRequestStatus('idle')
            }
           
        }
    }




 



    return (
       
    <section>
        <h2>Add a new post</h2>
       
        <form>
            <div>
            <label htmlFor="postTitle">Post Title: </label>
            <input 
                type="text"
                id="postTitle"
                name="postTitle"
                value={title}
                onChange={onTitleChanged}
            />
            </div>

            <div>
            <label htmlFor="postContent">Content: </label>

            <textarea 
                id="postContent"
                name="postContent"
                value={text}
                onChange={onTextChanged}
            />
            </div>

            <div>
            <label htmlFor="postAuthor">Author: </label>
            <textarea 
                id="postContent"
                name="postContent"
                value={author}
                
            />
            </div>

            <div>
            <label htmlFor="postAuthor">Image: </label>
            <textarea 
                id="postImaget"
                name="postImage"
                value={image}
                onChange={onImageChanged}
            />
            </div>

            




        </form>
        


            <button 
                type="button"
                onClick={onSaveClicked}
                disabled={!user}
                
            >
                Save Post 
            </button>

            {!user? <p>You must be logged in order to add a post.</p>: <p>Share your thoughts with the world.</p>}


           

          
    </section>

 

    )
} 

export default Form
