import { useSelector, useDispatch } from "react-redux"
import { useEffect } from "react"
import {selectAllPosts, getPostsStatus, getPostsError, fetchPosts} from '../features/posts/postsSlice'

import { createAsyncThunk } from "@reduxjs/toolkit"
import axios from "axios"

import PostExcerpt from "./PostExcerpt"



const PostsList = () => {

    const POSTS_URL2 = 'http://localhost:5000/api/posts'

    const fetchPosts2 = async () => {
        try {
            const response = await axios.get(POSTS_URL2)
            console.log(response.data)
            return response.data
        } catch (error) {
            return error.message
        }
    }


    const dispatch = useDispatch()

    const posts = useSelector(selectAllPosts)
    const postsStatus = useSelector(getPostsStatus)
    const postsError = useSelector(getPostsError)

  
    useEffect(() => {
        fetchPosts2()
        if (postsStatus === 'idle') {
            dispatch(fetchPosts())
        }
    }, [postsStatus, dispatch])




    let postsContent
    switch (postsStatus) {
        case 'loading':
            postsContent = <p>"Loading..."</p>
            break
        case 'succeeded':
            postsContent = posts.map( post => <PostExcerpt key = {post.id} post = {post}/>)
            break
        case 'failed':
            postsContent = <p>{postsError}</p>
            break
        default:
            postsContent = <p>"Loading...default"</p>
           

    }


    return (

        <article>

       
        <h2>*** PostsList ***</h2>
        {postsContent}
        <h2>*** End of PostsList ***</h2>
       
        </article>



    )
} 

export default PostsList
