import { useState } from 'react'
import {NavLink} from 'react-router-dom'
import { FaThumbsDown, FaThumbsUp } from 'react-icons/fa'



const PostExcerpt = ( {post}) => {

  
    const [fullPost, setFullPost ] = useState(false) 

    const handleShowFullPost = () => {
        setFullPost((prevState) => !prevState)
    }

    const excerptMaxLength = 400
    const postLength = post.text.length

    const isShowMoreButton = postLength > excerptMaxLength

    return (
        // <article key={post.id}>
        <article style={{"transition-duration": "1500ms"}}>
            {post._id} <br/>
            <img src={post.image} alt="img" width='500' height='300'></img>
            {post.title? <h3>{post.title}</h3> : <h3>ni ma tytulu</h3>}
            {post.text? <p style={{"transition-duration": "1500ms"}}>{!fullPost? (post.text.substring(0, excerptMaxLength)) : post.text}</p> : <h3>ni ma cia≈Ça postu</h3>}
            <NavLink to={`/userprofile/${post.author}`}>
            {post.author? (<p>{post.author}</p>): <p>unknown to mankind</p>}
            </NavLink>
            
            <p>{post.createdAt}</p>
            <FaThumbsUp/><p>I like that!</p>
            <FaThumbsDown/><p>Don't like that</p>
            {isShowMoreButton&&(<button onClick={handleShowFullPost}>{fullPost? 'Collapse post' : 'Expand post'}</button>)}
            <hr/>
                    
        </article>
    )
}

export default PostExcerpt
