import styled from 'styled-components'

export const StyledArticle = styled.article`
   padding: 1rem;
   background-color: white;
   max-width: 1200px;
   margin: 2rem 0rem;
   border-radius: 20px;

   img {
      border-radius: 20px;
     
   }
   .image {
      height: 300px;
    width: 500px;
   }
   img:before {
    content: ' ';
    display: block;
    position: absolute;
    height: 300px;
    width: 500px;
    background-image: url('https://st3.depositphotos.com/23594922/31822/v/600/depositphotos_318221368-stock-illustration-missing-picture-page-for-website.jpg');
}
`
