import styled from 'styled-components'

export const StyledExcerpt = styled.div`
   padding: 2rem 3rem;
   margin: 1rem;

   a {
    color: red;
    text-decoration: none;
   }
   button {
    background: rgb(63,94,251);
    background: linear-gradient(142deg, rgba(63,94,251,1) 0%, rgba(252,70,107,0.05085784313725494) 100%);padding: 0.3rem 1rem;
        transition-duration: 500ms;
        border-radius: 20px;
        border: none;
      
    } 
    button:hover {
        background-color: #019ae9;
        color: white;
    }
`
