import { useState } from 'react'
import {NavLink} from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { deleteOwnedPost } from '../features/posts/postsSlice'
import { FaThumbsDown, FaThumbsUp } from 'react-icons/fa'
import { StyledReactions } from '../styles/Reactions.styled'
import { StyledArticle } from '../styles/Article.styled'
import { StyledExcerpt } from '../styles/Excerpt.styled'
import Form from './Form'



const PostExcerpt = ( {post}) => {

    const dispatch = useDispatch()

    const [fullPost, setFullPost ] = useState(false) 

    const [ editing, setEditing] = useState( false)

    const handleShowFullPost = () => {
        setFullPost((prevState) => !prevState)
    }


    const handleUpdatePost = () => {
        alert('updated')
        setEditing(true)
    }

    const excerptMaxLength = 400
    const postLength = post.text.length
    const isShowMoreButton = postLength > excerptMaxLength

    const styleUp = { color: "#57cee6", transform: 'scale(2, 2)', cursor: 'pointer' }
    const styleDown = { color: "#ff5c8a", transform: 'scale(2, 2) translateY(20%)', cursor: 'pointer' }





    const shortTitle = ( title) => {
        const shortTitle = title.substring(0,15).toUpperCase()
        return shortTitle
    }

    return (
        // <article key={post.id}>
        <StyledArticle >
            
            
            <div className='image'>
            {post._id} <br/>
            <img src={post.image} alt=" " width='500' height='300'></img>
            </div>

            
            
            
            






            {editing === false && (

            <StyledExcerpt>

            <div >
            {post.title? <h3>{post.title}</h3> : <h3>ni ma tytulu</h3>}
            {post.text? <p style={{"transitionDuration": "15000ms"}}>{!fullPost? (post.text.substring(0, excerptMaxLength)) : post.text}</p> : <h3>ni ma cia≈Ça postu</h3>}
            </div>

            <StyledReactions>

            <div className="flex">
            <NavLink to={`/userprofile/${post.author}`}>
            {post.author? (<p>{post.author}</p>): <p>unknown to mankind</p>}
            </NavLink>
            <p>{post.createdAt}</p>
            </div>

            <div className="flex">
            <p><FaThumbsUp style={styleUp} /></p><p>2334</p>
            <p><FaThumbsDown style={styleDown}/></p><p>233</p>
            {isShowMoreButton&&(<button onClick={handleShowFullPost}>{fullPost? 'Collapse post' : 'Expand post'}</button>)}
            </div>

            </StyledReactions>
            <div className="flex">
            <p onClick={() => {
                dispatch(deleteOwnedPost(post._id))
            }
                }>delete post</p> 
            <p onClick={handleUpdatePost}>update post</p>
            </div>
            <hr style={{"margin": "2rem 2rem"}}/>
            </StyledExcerpt>

            )}

            { editing && <Form /> }
            













            <p className='shortTitle'>{shortTitle(post.title)}</p>
           <div className="gradientCircle"></div>

        </StyledArticle>
    )
}

export default PostExcerpt
