import { useEffect } from "react"
import { useDispatch } from "react-redux";
import { getPostsByUser } from "../../../server/controllers/postController";
const UserProfile = () => {

    const dispatch = useDispatch()

    useEffect(() => {
        if (userId) {
          dispatch(getPostsByUser(userId));
        }
    }, [])

    return (
        <>
        <h1>This is user's profile of USER</h1>
        <p>User's description</p>
        <p>Show user's posts</p>
        <p>Show user's pcomments</p>
        </>
    )
} 

export default UserProfile
