import { useEffect } from "react"
import { useDispatch } from "react-redux"
import { useParams } from "react-router-dom"
import PostsList from "./PostsList"
import { useSelector } from "react-redux"
import { selectAllPostsOfUser, getPostsStatus, getPostsError, fetchPostsByUser } from "../features/posts/postsSlice"


const UserProfile = () => {

    const dispatch = useDispatch()
    const posts = useSelector(selectAllPostsOfUser)
    const reversedPosts = [...posts].reverse()
    const postsStatus = useSelector(getPostsStatus)
    const postsError = useSelector(getPostsError)

  
    useEffect(() => {
        if (postsStatus === 'idle') {
            dispatch(fetchPostsByUser())
        }
    }, [postsStatus, dispatch])




    let postsContent
    switch (postsStatus) {
        case 'loading':
            postsContent = <p>"Loading..."</p>
            break
        case 'succeeded':
            postsContent = reversedPosts.map( post => <PostExcerpt key = {post._id} post = {post}/>)
            break
        case 'failed':
            postsContent = <p>{postsError}</p>
            break
        default:
            postsContent = <p>"Loading...default"</p>
           

    }







