import styled from 'styled-components'

export const StyledArticle = styled.article`
   padding: 1rem;
   background-color: white;
   box-shadow: 3px 3px 3px 2px rgba(0, 0, 0, 0.5);
   max-width: 1200px;
   margin: 2rem 0rem;
   border-radius: 20px;
   position: relative;
   overflow: hidden;
   &:hover{
      box-shadow: 5px 5px 5px 4px white;
   }
   img {
      border-radius: 20px;
     
   }
   .image {
      height: 300px;
    width: 500px;
   }
   img:before {
    content: ' ';
    border-radius: 20px;
    position: absolute;
    height: 300px;
    width: 500px;
    background-image: url('https://www.atpine.com.au/wp-content/uploads/2016/12/no-image-available.jpg');
}
.gradientCircle{
   background: rgb(221,96,68);
background: radial-gradient(circle, rgba(221,96,68,0.13209033613445376) 3%, rgba(255,255,255,0.053658963585434205) 70%);
height: 20vh;
width: 20vh;
position: absolute;
top: -40px;
right: -20px;
transform: scale(2);

}
.gradientCircle:nth-of-type(2){
   background: green;
}
`
