import { useState } from 'react'
import {NavLink} from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { deleteOwnedPost, updateOwnedPost, likePost, dislikePost } from '../features/posts/postsSlice'
import { FaThumbsDown, FaThumbsUp, FaTrash, FaRegEdit } from 'react-icons/fa'
import { StyledReactions } from '../styles/Reactions.styled'
import { StyledArticle } from '../styles/Article.styled'
import { StyledExcerpt } from '../styles/Excerpt.styled'
import { useSelector } from 'react-redux'
import { StyledUpdateForm } from '../styles/UpdateForm.styled'

const SinglePost = ( {post}) => {

    ///rozdziel na dwa komponenty --> excerptData i excerptEdit ( w zależności czy editing działa)
    // form do updatu w osobnym komponencie koniecznie!!!!   <<<<<<<<<=========
    
    const { user } = useSelector((state) => state.users)
    const nickname = user? user.nickname : null


    const [postData, setPostData ] = useState({
        title: post.title,
        text: post.text,
        image: post.image,
        author: post.author,
        _id: post._id,
        createdAt: post.createdAt,
        likes: post.likes,
        dislikes: post.dislikes,
        likers: post.likers,
        dislikers: post.dislikers
        
      })    

    const id = post._id


    const dispatch = useDispatch()

    const [fullPost, setFullPost ] = useState(false) 

    const [ editing, setEditing] = useState( false)

    const handleShowFullPost = () => {
        setFullPost((prevState) => !prevState)
    }


    const handleUpdatePost = () => {
       
        setEditing((prevState) => !prevState)

      
        if (editing) {
          
            dispatch(updateOwnedPost(postData))
        }
    }

    const handleLikePost = () => {
        dispatch(likePost({id, nickname}))
        
    }

    const handleDislikePost = () => {
        dispatch(dislikePost({id, nickname}))
    }

    const excerptMaxLength = 400
    const postLength = post.text.length
    const isShowMoreButton = postLength > excerptMaxLength

    const styleUp = { color: "#57cee6", transform: 'scale(2, 2)', cursor: 'pointer' }
    const styleDown = { color: "#ff5c8a", transform: 'scale(2, 2) translateY(20%)', cursor: 'pointer' }

    const shortTitle = ( title) => {
        const shortTitle = title.substring(0,15).toUpperCase()
        return shortTitle
    }

    //to do formu 
    const onPostFormChange = (e) => {
        setPostData((prevState) => ({
          ...prevState,
          [e.target.name]: e.target.value,
        }))  
      }  

    //   const onSaveClicked = () => {
      
    //     if (canSave) {
    //         try {
    //             setAddRequestStatus('pending')
    //             dispatch(addNewPost(postData))

    //             setPostData({
    //                 title: '',
    //                 text: '',
    //                 image: '',
    //                 author: creator,
    //               })    
               
    //         } catch (err) {
    //             console.error('Failed to save the post', err)
    //         } finally {
    //             setAddRequestStatus('idle')
    //         }
           
    //     }
    // }


    const isOwner = nickname === post.author
    const editOptions = isOwner? (
        <div className="flex">
                    
        <p style={{cursor: 'pointer', color: '#ff5c8a', transform: 'scale(1.4)'}} onClick={() => {
            dispatch(deleteOwnedPost(post._id))}}
        ><FaTrash /></p> 

        <p style={{cursor: 'pointer', color: '#80c99c',transform: 'scale(1.4)'}}onClick={ handleUpdatePost }
        ><FaRegEdit /></p>

        </div>
    ) : null


    return (
        // <article key={post.id}>
        <StyledArticle >
            
            
            <div className='image'>
            {/* {post._id} <br/> */}
            <img src={post.image} alt=" " width='500' height='300'></img>
            </div>

            
            
            
            






            {!editing && (

            <StyledExcerpt>

            <div >
            {post.title? <h3>{post.title}</h3> : <h3>no title available</h3>}
            {post.text? <p style={{"transitionDuration": "150ms"}}>{!fullPost? (post.text.substring(0, excerptMaxLength)) : post.text}</p> : <h3>ni ma ciała postu</h3>}
            </div>

            <StyledReactions>

            <div className="flex">
            <NavLink to={`/userprofile/${post.author}`}>
            {post.author? (<p>{post.author}</p>): <p>unknown to mankind</p>}
            </NavLink>
            <p>{post.createdAt}</p>
            </div>

            <div className="flex">
            <p onClick={handleLikePost}><FaThumbsUp style={styleUp} /></p><p>{post.likes? post.likes.toString() : 0}</p>
            <p onClick={handleDislikePost}><FaThumbsDown style={styleDown}/></p><p>{post.dislikes? post.dislikes.toString() : 0}</p>
            {isShowMoreButton&&(<button onClick={handleShowFullPost}>{fullPost? 'Collapse post' : 'Expand post'}</button>)}
            </div>

            </StyledReactions>
           
                {editOptions}
               
            


            <hr style={{"margin": "2rem 2rem"}}/>

            { (post.likers.length > 0 ) && (

<p>Liked this post: {post.likers.map((liker) => {
    return(
        <NavLink key={liker} to={`/userprofile/${liker}`}> {liker}</NavLink>
    )
})}</p>

)}

{ (post.dislikers.length > 0 ) && (

<p>Haters: {post.dislikers.map((disliker) => {
    return(
        <NavLink key={disliker} to={`/userprofile/${disliker}`}> {disliker}</NavLink>
    )
})}</p>

)}






 

            </StyledExcerpt>

            )}



            { editing && ( 



<StyledUpdateForm>

<form>
    <div>
    <label htmlFor="postTitle">Post Title: </label>
    <input 
        type="text"
        id="title"
        name="title"
        value={postData.title}
        onChange={onPostFormChange}
    />
    </div>

    <div>
    <label htmlFor="postContent">Content: </label>

    <textarea 
        id="text"
        name="text"
        value={postData.text}
        onChange={onPostFormChange}
        rows="3"
    />
    </div>


    <div>
    <label htmlFor="postImage">Image: </label>
    <textarea 
        id="image"
        name="image"
        value={postData.image}
        onChange={onPostFormChange}
        placeholder='Paste here your image url'
    />
    </div>

    
    {/* <StyledFlex>
    <label htmlFor="postAuthor">Author: {user? author : null} </label>
    </StyledFlex> */}
   
    



    <StyledReactions>
    <button 
        type="button"
        onClick={handleUpdatePost}
        disabled={!user}
        
        >
        Update Post 
    </button>
   

    </StyledReactions>
</form>

</StyledUpdateForm>







            ) }
                        <p className='shortTitle'>{shortTitle(post.title)}</p>
           <div className="gradientCircle"></div>
        </StyledArticle>
    )
}

export default SinglePost
