import { useEffect } from "react"
import { useDispatch } from "react-redux"
import { useParams } from "react-router-dom"

const UserProfile = () => {

    const dispatch = useDispatch()
    const { nickname } = useParams()

    // useEffect(() => {
    //     if (userId) {
    //       dispatch(getPostsByUser(userId));
    //     }
    // }, [])


    const posts = useSelector(selectAllPostsOfUser)
    const reversedPosts = [...posts].reverse()
    const postsStatus = useSelector(getPostsStatus)
    const postsError = useSelector(getPostsError)

  
    useEffect(() => {
        if (postsStatus === 'idle') {
            dispatch(fetchPosts())
        }
    }, [postsStatus, dispatch])




    let postsContent
    switch (postsStatus) {
        case 'loading':
            postsContent = <p>"Loading..."</p>
            break
        case 'succeeded':
            postsContent = reversedPosts.map( post => <PostExcerpt key = {post._id} post = {post}/>)
            break
        case 'failed':
            postsContent = <p>{postsError}</p>
            break
        default:
            postsContent = <p>"Loading...default"</p>
           

    }







    return (
        <>
        <h1>{`This is users profile of ${nickname}`}</h1>
        <p>User's description</p>
        <p>Show user's posts</p>
        <p>Show user's pcomments</p>
        </>
    )
} 

export default UserProfile
