import styled from 'styled-components'

export const StyledArticle = styled.article`
   padding: 1rem;
   background-color: white;
   max-width: 1200px;
   margin: 2rem 0rem;
   border-radius: 20px;

   img {
      border-radius: 20px;
     
   }
   .image {
      height: 300px;
    width: 500px;
   }
   img:before {
    content: ' ';
    position: absolute;
    height: 300px;
    width: 500px;
    background-image: url('https://media.istockphoto.com/photos/no-image-available-picture-id531302789?k=20&m=531302789&s=612x612&w=0&h=sZC5tznKIqoFX4xPl7OYNaf_oO-DnGjGWc-gUOjydkk=g');
}
`
