import { useEffect } from "react"
import { useDispatch } from "react-redux"
import { useParams } from "react-router-dom"
import PostsList from "./PostsList"
import { useSelector } from "react-redux"
import { selectAllPostsOfUser, getPostsStatus, getPostsError, fetchPostsByUser } from "../features/posts/postsSlice"
import PostExcerpt from "./PostExcerpt"


const UserProfile = () => {

    const params = useParams()
    const { nickname } = params

    const dispatch = useDispatch()
    const userPosts = useSelector(selectAllPostsOfUser)
    const reversedPosts = [...userPosts].reverse()
    const postsStatus = useSelector(getPostsStatus)
    const postsError = useSelector(getPostsError)

  
    useEffect(() => {
        console.log(userPosts)
        if (postsStatus === 'idle') {
            dispatch(fetchPostsByUser({nickname}))
        }
    }, [postsStatus, dispatch, nickname, params])




    let postsContent
    switch (postsStatus) {
        case 'loading':
            postsContent = <p>"Loading..."</p>
            break
        case 'succeeded':
            postsContent = reversedPosts.map( post => <PostExcerpt key = {post._id} post = {post}/>)
            break
        case 'failed':
            postsContent = <p>{postsError}</p>
            break
        default:
            postsContent = <p>"Loading...default"</p>
           

    }







    return (
        <>
        <p>User's description</p>
        <p>Show user's posts</p>
        <p>Show user's pcomments</p>
        <h1>{`This is users profile of ${nickname}`}</h1>
      
        {postsContent}
        </>
    )
} 

export default UserProfile
