import { createSlice, nanoid, createAsyncThunk} from "@reduxjs/toolkit";
import postsService from "./postsService";


const initialState = {
   posts: [],
   userPosts: [],
   status: 'idle',
   error: null,
   createPostStatus: 'idle'
}

export const fetchPosts = createAsyncThunk(
    'posts/fetchPosts', 
    async (_, thunkAPI) => {
    try {
        return await postsService.getAllPosts()
    } catch (error) {
        const message =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString()
        return thunkAPI.rejectWithValue(message)
      }
})


export const addNewPost = createAsyncThunk(
    'posts/addNewPost',
    async (postData, thunkAPI) => { 
      try {
        const token = thunkAPI.getState().users.user.token
        return await postsService.createPost(postData, token)
      } catch (error) {
        const message =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString()
        return thunkAPI.rejectWithValue(message)
      }
    }
  )

  export const fetchPostsByUser = createAsyncThunk(
    'posts/fetchPostsByUser',
    async (req, res, thunkAPI) => { 
        const { nickname } = req
        console.log(req)
      try {
        return await postsService.getPostsByUser(nickname)
      } catch (error) {
        const message =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString()
        return thunkAPI.rejectWithValue(message)
      }
    }
  )







const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        // spróbuj wykasować post added
        postAdded: {
            reducer(state, action) {
                console.log(action.payload)
                state.posts.concat([action.payload])
                },
            prepare(title, text, image, author) {
                return {
                    payload: {
                    _id: nanoid(),
                    title,
                    text,
                    author,
                    image,
                    createdAt: new Date().toISOString()               
                }
                }
            }
        },
    },
    extraReducers(builder) {
        builder
            //fetchPosts
            .addCase(fetchPosts.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchPosts.fulfilled, (state, action) => {
                state.status = 'succeeded'
                
                const loadedPosts = action.payload
                state.posts = state.posts.concat(loadedPosts)
            })
            .addCase(fetchPosts.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
            //addNewPost
            .addCase(addNewPost.pending, (state, action) => {
                state.createPostStatus = 'loading'
                
            })
            .addCase(addNewPost.fulfilled, (state, action) => {
                state.createPostStatus = 'succeed'
                
               
                    state.posts.push(action.payload)
            })
            .addCase(addNewPost.rejected, (state, action) => {
                state.createPostStatus = 'failed'
                state.error = action.error.message
            })
            //fetchPostsByUser
            .addCase(fetchPostsByUser.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchPostsByUser.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.userPosts.concat([...action.payload])
            })
            .addCase(fetchPostsByUser.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })






    }
})

export const selectAllPosts = (state) => state.posts.posts
export const selectAllPostsOfUser = (state) => state.posts.userPosts
export const getPostsStatus = (state) => state.posts.status
export const getPostsError = (state) => state.posts.error

export const {postAdded} = postsSlice.actions

export default postsSlice.reducer
