import { useSelector, useDispatch } from "react-redux"
import { useEffect } from "react"
import {selectAllPosts, getPostsStatus, getPostsError, fetchPosts} from '../features/posts/postsSlice'

import PostExcerpt from "./PostExcerpt"

const PostsList = () => {
    const dispatch = useDispatch()

    const posts = useSelector(selectAllPosts)
    const postsStatus = useSelector(getPostsStatus)
    const postsError = useSelector(getPostsError)

    useEffect(() => {
        if (postsStatus === 'idle') {
            dispatch(fetchPosts())
        }
    }, [postsStatus, dispatch])



    let postsContent
    switch (postsContent) {
        case 'loading':
            postsContent = <p>"Loading..."</p>
            break
        case 'succeeded':
            postsContent = posts.map( post => <PostExcerpt key = {post.id} post = {post}/>)
            break
        case 'failed':
            postsContent = <p>{postsError}</p>
            break
        default:
            postsContent = <p>"Loading...default"</p>

    }


    return (

        <article>

       
        <h2>*** PostsList ***</h2>
        {postsContent}
        <h2>*** End of PostsList ***</h2>
       
        </article>



    )
} 

export default PostsList
